import axios from "axios";

export const createGoods = (name, cost, descript, file) => {
    return async dispatch => {
        try{
            const response = await axios.post("http://localhost:5000/api/goods/", {
                name,
                cost,
                descript,
                file
            })
            alert(response.data.message)
        } catch(e){
            alert(e.response.data.message)
        }
        
    }
}
