import React, { Component } from 'react';
//import React, {useState} from 'react';
import CardList from '../Components/Items/CardList'
import Create from '../Components/Goods/Create';
import Good from '../Components/Items/Good'
import { Redirect } from 'react-router-dom';

export default class Admin extends Component {
    state = {
        allCards: [],
        status: false,
        activeItem: [],
        redirect: false
    }

    componentDidMount() {
        if(this.props.typeUser){
            const apiUrl = 'http://localhost:5000/api/goods';
            fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {
                let mass = []
                for(let item in data['goods']) {
                    mass.push(data['goods'][item])
                }

                this.setState({allCards: mass.reverse()})
            });
        } if(this.props.typeUser && localStorage.getItem('token')) {
            this.setState({redirect: true})
        }
    }
    
    setActiveItem=(data)=>{
      this.setState({activeItem: data})
      this.setState({status: !this.state.status})
    }

    renderRedirect = () => {
        if (this.state.redirect) {
          return <Redirect to='/' />
        }
    }

    render(){
        return (
            <div className='admin'>
                {this.renderRedirect()}
                {!this.state.status && <Create/>}
                {!this.state.status && <CardList data={this.state.allCards} activeItem={this.setActiveItem}/>}
                {(this.state.status && Object.keys(this.state.activeItem).length !== 0) && <Good data={this.state.activeItem}/>}
            </div>      
        )
    }
}
