import React, { Component } from 'react';
import { withRouter } from "react-router";
//import React, {useState} from 'react';
import CardList from '../Components/Items/CardList'
import Create from '../Components/Goods/Create';
import Good from '../Components/Items/Good'
import Card from '../Components/Items/Card'

class Item extends Component {
    state = {
        item: []
    }

    componentDidMount() {
        const id = this.props.match.params.id;
        console.log(id)
        const apiUrl = `http://localhost:5000/api/goods/${id}`;
        fetch(apiUrl)
           .then((response) => response.json())
           .then((data) => {
            this.setState({item: data['goods'][0]})
        });
        
    }
    render(){
        return (
            <div>
                <Card 
                    id={this.state.item._id}
                    name={this.state.item.name}
                    cost={this.state.item.cost}
                    img={this.state.item.img}
                    descript={this.state.item.descript}
                    type={false}
                />
            </div>  
        )
    }
}

export default withRouter(Item);
