import axios from "axios";

export const createGoods = (name, cost, descript, file) => {
    return async dispatch => {
        try{
            let formData = new FormData()
            formData.append('name', name)
            formData.append('cost', cost)
            formData.append('descript', descript)
            formData.append('file', file)
            const response = await axios.post("http://localhost:5000/api/goods", formData)
            alert(response.data.message)
        } catch(e){
            alert(e.response.data.message)
        }
        
    }
}

export const buyGoods = (userId, itemId) => {
    return async dispatch => {
        try{
            const response = await axios.post(`http://localhost:5000/${userId}`, {itemId})
            alert(response.data.message)
        } catch(e){
            alert(e.response.data.message)
        }
        
    }
}

export const changeGoods = (id, name, cost, descript, file) => {
    return async dispatch => {
        try{
            let formData = new FormData()
            formData.append('id', id)
            formData.append('name', name)
            formData.append('cost', cost)
            formData.append('descript', descript)
            formData.append('file', file)
            const response = await axios.put("http://localhost:5000/api/goods/", formData)
            alert(response.data.message)
        } catch(e){
            alert(e.response.data.message)
        }
        
    }
}
