import React, {useState, useEffect} from 'react';
import {useDispatch} from "react-redux";
import {changeGoods, deleteGoods} from "../../actions/goods.js";

const Good = (data) => {
    const [name, setName] = useState("")
    const [cost, setCost] = useState("")
    const [descript, setDescript] = useState('');
    const [file, setFile] = useState('');
    const dispatch = useDispatch()


    useEffect(() => {
        setName(data.data.name)
        setCost(data.data.cost)
        setDescript(data.data.descript)
    }, []);

    const changeHandler = (event) => {
		setFile(event.target.files[0]);
	};

    return (
        <div>
            <form >
                <div className='card__picture'>
                    <img src={`http://localhost:5000/files/${data.data.img}`} alt={data.data.name}/>
                </div>
                <input type="file" onChange={changeHandler} name="file" />
                <input onChange={e => setName(e.target.value)} value={name} type="text"/>
                <input onChange={e => setCost(e.target.value)} value={cost} type="text"/>
                <input onChange={e => setDescript(e.target.value)} value={descript} type="text"/>
                <button type="submit" onClick={() => {dispatch(changeGoods(data.data.id, name, cost, descript, file))}}>Изменить товар</button>
            </form>
            <div><button type="submit" onClick={()=>{dispatch(deleteGoods(data.data))}}>Удалить товар</button></div>
        </div>
    )
}

export default Good;
