import React, { Component } from 'react';
import CardList from '../Components/Items/CardList'
import Searcher from '../Components/Searcher';

export default class Catalog extends Component {
    state = {
        apiUrl: 'http://localhost:5000/api/goods',
        status: false,
        all: []
    }

    loadFile(){
        fetch(this.state.apiUrl)
        .then((response) => response.json())
        .then((data) => {
            let mass = []
            for(let item in data['goods']) {
                mass.push(data['goods'][item])
            }
            this.setState({status: false})
            this.setState({all: mass.reverse()})
        });
    }

    componentDidMount() {
        this.loadFile()
    }

    componentDidUpdate(){
        if(this.state.status){
            this.loadFile()
        }
    }
    
    setActiveItem=(data)=>{
        this.props.history.push(`/goods/${data.id}`)
    }

    searchedGoods=(data)=>{
        if(data.trim().length!==0){
            this.setState({apiUrl: `http://localhost:5000/api/goods/name/${data}`})
        } else {
            this.setState({apiUrl: `http://localhost:5000/api/goods`})
        }
        this.setState({status: !this.state.status})
    }

    render(){
        return (
            <div className='supply'>
                 <div className='main__collection-title catalog__title'>КАТАЛОГ</div>
                <Searcher searchedGoods={this.searchedGoods}/>
                <CardList
                    data={this.state.all}
                    activeItem={this.setActiveItem}
                />
            </div>
        )
    }
}
