import React, { Component } from 'react';
import { Link,  withRouter } from 'react-router-dom';
import call from '../img/eva_phone-outline.png';
import PopUp from './PopUp';
import  { registration,login } from '../actions/user'
import Login from './authorization/Login'
import axios from "axios";
import Reqistration from './authorization/Registration';
// const MongoClient = require("mongodb").MongoClient;
// const url = "mongodb://localhost:5000/";
// const mongoClient = new MongoClient(url);


export default withRouter(class Header extends Component {
    state={
        scroll: false,
        modalActive: false,
        email: '',
        password: '',
        copyPassword: '',
        emailDirty: false,
        passwordDirty: false,
        copyPasswordDirty: false,
        emailError: 'Еmail не может быть пустым',
        passwordError: 'Password не может быть пустым',
        copyPasswordError: 'Пароли должны совпадать',
        formValid: false,
        authType: ''
    }

    auth(){
        console.log(this.state.authType)
        if(this.state.authType){
            return(<Reqistration/>)
        } else {
            return(<Login/>)
        }
    }
    
    burgerMenu(){
        document.body.classList.toggle('_lock');
        document.querySelector('.menu__icon').classList.toggle('_active');
        document.querySelector('.menu__body').classList.toggle('_active');
    }
        
    handleLinkClick =()=> {
        let element = document.getElementById('hook');
        if(element === null){
            this.setState({scroll: true});
            this.props.history.push("/")
        } else{
            element.scrollIntoView();
        }
    };

    setModalActive = (active, type) => {
        this.setState({modalActive: active});
        if(type === true || type === false){
            this.setState({authType: type});
        }
    }

    blurHandler =(e)=>{
        switch (e.target.name) {
            case 'email':
                this.setEmailDirty(true)
                break;
            case 'password':
                this.setPasswordDirty(true)
                break;
            case 'copyPassword':
                this.setCopyPasswordDirty(true)
                break;
            default:
                break;
        }

        if(this.state.emailError || this.state.passwordError || (this.state.authType === 'РЕГИСТРАЦИЯ' && this.state.copyPasswordError)) {
            this.setFormValid(false)
        } else {
            this.setFormValid(true)
        }
    }

    setEmail =(text)=>{
        this.setState({email: text});
    }

    setPassword=(text)=>{
        this.setState({password: text});
    }

    setCopyPassword=(text)=>{
        this.setState({copyPassword: text});
    }

    setEmailDirty=(active)=>{
        this.setState({emailDirty: active});
    }

    setPasswordDirty=(active)=>{
        this.setState({passwordDirty: active});
    }

    setCopyPasswordDirty=(active)=>{
        this.setState({copyPasswordDirty: active});
    }

    setEmailError=(active)=>{
        this.setState({emailError: active});
    }

    setPasswordError=(active)=>{
        this.setState({passwordError: active});
    }

    setCopyPasswordError=(active)=>{
        this.setState({copyPasswordError: active});
    }

    setFormValid = (active) => {
        this.setState({formValid: active});
    }

    emailHandler = (e) =>{
        this.setEmail(e.target.value)
        const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if (!re.test(String(e.target.value).toLowerCase())){
            this.setEmailError('Некорректный емейл')
        } else {
            this.setEmailError('')
        }

        if(this.state.emailError || this.state.passwordError || (this.state.authType === 'РЕГИСТРАЦИЯ' && this.state.copyPasswordError)) {
            this.setFormValid(false)
        } else {
            this.setFormValid(true)
        }
    }

    copyPasswordHandler = (e) =>{
        this.setCopyPassword(e.target.value)
        if(!e.target.value){
            this.setCopyPasswordError('Copy Password не может быть пустым')     
        } else if(e.target.value !== this.state.password) {
            this.setCopyPasswordError('Пароли должны совпадать')
        } else {
            this.setCopyPasswordError('')
        }
        
        if(this.state.emailError || this.state.passwordError || (this.state.authType === 'РЕГИСТРАЦИЯ' && this.state.copyPasswordError)) {
            this.setFormValid(false)
        } else {
            this.setFormValid(true)
        }
    }

    passwordHandler = (e) =>{
        this.setPassword(e.target.value)
        if(e.target.value.length < 3 || e.target.value.length > 15){
            this.setPasswordError('Пароль должен быть длинее 3 и меньше 15')
            if(!e.target.value){
                this.setPasswordError('Password не может быть пустым')
            } 
        } else {
            this.setPasswordError('')
        }

        if(this.state.emailError || this.state.passwordError || (this.state.authType === 'РЕГИСТРАЦИЯ' && this.state.copyPasswordError)) {
            this.setFormValid(false)
        } else {
            this.setFormValid(true)
        }
    }

    componentDidUpdate(prevProps) {
        if (this.state.scroll) {
            let element = document.getElementById('hook');
            element.scrollIntoView();
            this.setState({scroll: false});
        }
        this.renderPosts()

    }

    // renderPosts = mongoClient.connect(async function(err, client){
      
    //     const db = client.db("usersdb");
    //     const collection = db.collection("users");
     
    //     if(err) return console.log(err);
          
    //     collection.find().toArray(function(err, results){
                     
    //         console.log(results);
    //         client.close();
    //     });
    // });

    renderPosts = async () => {
        console.log('hi')
        await axios.get("http://localhost:5000/api/users").then(function (response) {
                    // handle success
                    console.log(response, 'hi');
                })
                .catch(function (error) {
                    // handle error
                    console.log(error, 'ss');
        })
        console.log('hit')
    }



    render(){
        return(
            <header className="header">
                <div className='header__info'>
                    <div className="header__container">
                        <div className='header__work'>
                            <a className='header__data' href='tel:+8(812)123-45-67'><img src={call} alt={call}/></a>
                            <a className='header__data' href='tel:+8(812)123-45-67'>8(812) 123-45-67</a>
                            <span className='header__data indent'>Работаем 7 дней в неделю</span>
                            <span className='header__data'>9:00 - 18:00</span>
                        </div>
                    </div>
                </div>
                <div className="header__block">
                    <div className="header__container">
                        <a href='/' className='header__logo'>PORTEN</a>
                        <div className='header__menu menu'>
                            <div className='menu__icon' onClick={this.burgerMenu}>
                                <span></span>
                            </div>
                            <nav className='menu__body'>
                                <ul className='menu__list'>
                                    <li>
                                        <Link to='/' className='menu__link'>ГЛАВНАЯ</Link>
                                        <Link to='/catalog' className='menu__link'>КАТАЛОГ</Link>
                                        <span onClick={this.handleLinkClick}className='menu__link'>ПОСТУПЛЕНИЕ</span>
                                        <span className='menu__link' onClick={()=>this.setModalActive(true, true)}>РЕГИСТРАЦИЯ</span>
                                        <span className='menu__link' onClick={()=>this.setModalActive(true, false)}>ПРОФИЛЬ</span>
                                    </li>
                                </ul>
                            </nav>  
                            <PopUp active={this.state.modalActive} setActive={this.setModalActive}>
                                {this.auth()}
                                {/* <form>
                                    <h1>{this.state.authType}</h1>
                                    {(this.state.emailDirty && this.state.emailError) && <div style={{color:'red'}}>{this.state.emailError}</div>}
                                    <input onChange={e => this.emailHandler(e)} value={this.state.email} onBlur={e => this.blurHandler(e)} name='email' type='text' placeholder='Enter your email'/>
                                    {(this.state.passwordDirty && this.state.passwordError) && <div style={{color:'red'}}>{this.state.passwordError}</div>}
                                    <input onChange={e => this.passwordHandler(e)} value={this.state.password} onBlur={e => this.blurHandler(e)} name='password' type='password' placeholder='Enter your password'/>
                                    {(this.state.authType==='РЕГИСТРАЦИЯ') && (this.state.copyPasswordDirty && this.state.copyPasswordError) && <div style={{color:'red'}}>{this.state.copyPasswordError}</div>}
                                    {(this.state.authType==='РЕГИСТРАЦИЯ') && <input onChange={e => this.copyPasswordHandler(e)} value={this.state.copyPassword} onBlur={e => this.blurHandler(e)} name='copyPassword' type='password' placeholder='Enter your password again'/>}
                                    <button disabled={!this.state.formValid} type='button' onClick={() => (this.state.authType==='РЕГИСТРАЦИЯ') ? registration(this.state.email, this.state.password): useDispatch(login(this.state.email, this.state.password))}>this.state.authType</button>
                                </form> */}
                            </PopUp>
                        </div> 
                    </div>
                </div>
            </header>
        )
    }
})
