import React, { Component } from 'react';
//import React, {useState} from 'react';
import CardList from '../Components/Items/CardList'
import Create from '../Components/Goods/Create';
import Good from '../Components/Items/Good'

export default class Admin extends Component {
    state = {
        allCards: [],
        status: false,
        //activeItem: []
    }

    componentDidMount() {
        const apiUrl = 'http://localhost:5000/api/goods';
        fetch(apiUrl)
          .then((response) => response.json())
          .then((data) => {
              let mass = []
            for(let item in data['goods']) {
                mass.push(data['goods'][item])
            }

            console.log(mass)

            this.setState({allCards: mass.reverse()})
        });
      }

    render(){
        return (
            <div className='admin'>
                {!this.state.status && <Create/>}
                {!this.state.status && <CardList data={this.state.allCards} activeItem={this.state.activeItem}/>}
                {this.state.status && <Good data={this.state.activeItem}/>}
            </div>      

        )
    }
}
