import React from 'react';
import { Link } from 'react-router-dom';
import {useDispatch} from "react-redux";
import { logout } from '../../reducers/userReducer';

const Li = (props) => {
    const dispatch = useDispatch()    

    return (
        <li>
            <Link to='/' className='menu__link'>ГЛАВНАЯ</Link>
            <Link to='/catalog' className='menu__link'>КАТАЛОГ</Link>
            <span onClick={props.action}className='menu__link'>ПОСТУПЛЕНИЕ</span>
            {!this.state.isAuth && <span className='menu__link' onClick={()=>this.setModalActive(true, true)}>РЕГИСТРАЦИЯ</span>}
            {!this.state.isAuth && <span className='menu__link' onClick={()=>this.setModalActive(true, false)}>ПРОФИЛЬ</span>}
            {this.state.isAuth && <span className='menu__link' /*onClick={}*/>ПРОФИЛЬ</span>}
            {this.state.isAuth && <span className='menu__link' onClick={()=>dispatch(logout())}>Выйти</span>}
        </li>
    )
}

export default Li
