import React, {useState} from 'react';
import {useDispatch} from "react-redux";
import {createGoods} from "../../actions/goods.js";


const Create = (props) => {
    const [name, setName] = useState("")
    const [cost, setCost] = useState("")
    const [descript, setDescript] = useState('');
    const [file, setFile] = useState(null);
    const dispatch = useDispatch()
    
    const changeHandler = (event) => {

		setFile(event.target.files[0]);


	};

    return (
            <form >
                <input onChange={e => changeHandler(e)} type="file" value={file} />
                <input onChange={e => setName(e.target.value)} type="text" value={name}/>
                <input onChange={e => setCost(e.target.value)} type="text" value={cost}/>
                <input onChange={e => setDescript(e.target.value)} type="text" value={descript}/>
                <button type="submit" onClick={() => {dispatch(createGoods(name, cost, descript, file))}}>gf</button>
            </form>
    )
}

export default Create
