import React, { Component } from 'react';
import CardList from '../Components/Items/CardList'
import Searcher from '../Components/Searcher';

export default class Catalog extends Component {
    state = {
        apiUrl: 'http://localhost:5000/api/goods',
        all: []
    }

    componentDidMount() {
        if(Object.keys(this.state.all).length===0){
            fetch(this.state.apiUrl)
            .then((response) => response.json())
            .then((data) => {
                let mass = []
                for(let item in data['goods']) {
                    mass.push(data['goods'][item])
                }

                this.setState({all: mass.reverse()})
            });
        }
    }
    
    setActiveItem=(data)=>{
        this.props.history.push(`/goods/${data.id}`)
    }

    searchedGoods=(data)=>{
        this.setState({all: data.reverse()})
    }

    render(){
        return (
            <div className='supply'>
                 <div className='main__collection-title catalog__title'>КАТАЛОГ</div>
                <Searcher/>
                <CardList
                    data={this.state.all}
                    activeItem={this.setActiveItem}
                />
            </div>
        )
    }
}
