import React, { Component } from 'react';
import CardList from '../Components/Items/CardList'
import Good from '../Components/Items/Good'
import { Redirect } from 'react-router-dom';

export default class Profile extends Component {
    state = {
        allCards: [],
        activeItem: [],
        redirect: false
    }

    componentDidMount() {
        if(this.props.typeUser==='user'){

            const apiUrl = `http://localhost:5000/api/user/${this.props.userId}/goods/`;
            fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {
                console.log(data['goods'])
                let mass = []
                // for(let item in data['goods']) {
                //     console.log(item)
                //     const goodsUrl = `http://localhost:5000/api/goods/${item}`
                //     fetch(apiUrl)
                //     .then((response) => response.json())
                //     .then((goods) => {
                //         console.log(goods)
                //     });
                // }
                // 
                // for(let item in data['goods']) {
                //     mass.push(data['goods'][item])
                // }

                this.setState({allCards: mass.reverse()})
            });
        } if(localStorage.getItem('token')===null || this.props.typeUser !=='user') {
            this.setState({redirect: true})
        }
    }
    
    setActiveItem=(data)=>{
        return <Redirect to={`/goods/${data.id}`} />
    }

    renderRedirect = () => {
        if (this.state.redirect) {
          return <Redirect to='/' />
        }
    }

    render(){
        return (
            <div className='user'>
                {this.renderRedirect()}
                {!this.state.status && <CardList data={this.state.allCards} activeItem={this.setActiveItem}/>}
            </div>      
        )
    }
}
